package recipe.converters.mongo;import lombok.Synchronized;import org.springframework.core.convert.converter.Converter;import org.springframework.lang.Nullable;import org.springframework.stereotype.Component;import recipe.commands.mongo.M_IngredientCmd;import recipe.domain.mongo.M_Ingredient;@Componentpublic class M_IngredientCmdToIngredient implements Converter<M_IngredientCmd, M_Ingredient> {    private M_UOMCmdToUOM cmdToUOM;    public M_IngredientCmdToIngredient(M_UOMCmdToUOM cmdToUOM) {        this.cmdToUOM = cmdToUOM;    }    @Synchronized    @Nullable    @Override    public M_Ingredient convert(M_IngredientCmd ingredientCmd) {        if (null == ingredientCmd) {            return null;        }        M_Ingredient ingredient = new M_Ingredient();        ingredient.setId(ingredientCmd.getId());        ingredient.setDescription(ingredientCmd.getDescription());        ingredient.setAmount(ingredientCmd.getAmount());        ingredient.setUnitOfMeasure(this.cmdToUOM.convert(ingredientCmd.getUnitOfMeasure()));        return ingredient;    }}