package recipe.converters.mongo;import com.mongodb.lang.Nullable;import lombok.Synchronized;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import recipe.commands.mongo.M_Note_Cmd;import recipe.domain.mongo.M_Note;@Componentpublic class M_NoteCommandToNote implements Converter<M_Note_Cmd, M_Note> {    @Synchronized    @Nullable    @Override    public M_Note convert(M_Note_Cmd note_cmd) {        if (null == note_cmd) {            return null;        }        final M_Note note = new M_Note(note_cmd.getRecipeNotes());        note.setId(note_cmd.getId());        return note;    }}