package recipe.domain.mongo;import lombok.Data;import org.springframework.data.mongodb.core.mapping.DBRef;import org.springframework.data.mongodb.core.mapping.Document;import recipe.domain.Recipe;import java.util.Set;/** * @author Zaynab osama */@Data@Documentpublic class M_Category extends Base {    private String description;    public M_Category(String description) {        this.description = description;    }    @DBRef    private Set<Recipe> recipes;}