package recipe.domain.mongo;import lombok.Data;import org.springframework.data.mongodb.core.mapping.DBRef;import java.math.BigDecimal;import java.util.UUID;@Datapublic class M_Ingredient {    private String id;    private String description;    private BigDecimal amount;    @DBRef    private M_UOM unitOfMeasure;    public M_Ingredient() {    }    public M_Ingredient(String description, BigDecimal amount, M_UOM MUnitOfMeasure) {        if (this.id == null || this.id.isEmpty()) {            this.id = UUID.randomUUID().toString();        }        this.description = description;        this.amount = amount;        this.unitOfMeasure = MUnitOfMeasure;    }}