package recipe.repositories;import org.junit.jupiter.api.Test;import org.junit.jupiter.api.extension.ExtendWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.autoconfigure.orm.jpa.DataJpaTest;import org.springframework.test.context.junit.jupiter.SpringExtension;import recipe.domain.Category;import recipe.domain.Difficulty;import recipe.domain.Recipe;import java.util.HashSet;import java.util.Set;import static org.junit.jupiter.api.Assertions.assertEquals;@ExtendWith(SpringExtension.class)@DataJpaTestclass CategoryRepositoryIT {    @Autowired    private CategoryRepository categoryRepository;    @Autowired    private RecipeRepository recipeRepository;    @Test    void recipesCategory() {        final Long id = 1l;        final Long secId = 2l;        final String category = "Egyptian";        Category egyptian = this.categoryRepository.findByDescription(category).get();        Recipe perfectGuacamole = new Recipe();        perfectGuacamole.setId(id);        perfectGuacamole.setCategory(egyptian);        Set<Recipe> recipeSet = new HashSet<>();        recipeSet.add(perfectGuacamole);        this.recipeRepository.saveAll(recipeSet);        egyptian.setRecipes(recipeSet);        assertEquals(1, (egyptian.getRecipes()).size());    }    @Test    void findBy_Description_Egyptian() {        final String desc = "Egyptian";        Category category = this.categoryRepository.findByDescription(desc).get();        assertEquals(desc, category.getDescription());    }    @Test    void findBy_Description_Italian() {        final String desc = "Italian";        Category category = this.categoryRepository.findByDescription(desc).get();        assertEquals(desc, category.getDescription());    }}