package recipe.repositories;import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;import org.junit.jupiter.api.extension.ExtendWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.autoconfigure.orm.jpa.DataJpaTest;import org.springframework.test.context.junit.jupiter.SpringExtension;import recipe.domain.Note;import java.util.Optional;import static org.junit.jupiter.api.Assertions.assertEquals;@ExtendWith(SpringExtension.class)@DataJpaTestclass NoteRepositoryIT {    @Autowired    private NoteRepository noteRepository;    private final Long idValue = 1l;    private final String note = "The trick to making perfect guacamole is using ripe avocados that are just the right amount of ripeness.  " +            "Not ripe enough and the avocado will be hard and tasteless. Too ripe and the taste will be off.";    private Note guacamoleNote;    @BeforeEach    void prepareNoteData() {        guacamoleNote = new Note();        guacamoleNote.setId(idValue);        guacamoleNote.setRecipeNotes(note);        this.noteRepository.save(guacamoleNote);    }    @Test    void deleteRecipeAndNote() {        this.noteRepository.delete(guacamoleNote);        Optional<Note> noteOptional = this.noteRepository.findById(idValue);        assertEquals(Optional.empty(), noteOptional);    }}