package recipe.repositories;import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;import org.junit.jupiter.api.extension.ExtendWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.autoconfigure.orm.jpa.DataJpaTest;import org.springframework.test.context.junit.jupiter.SpringExtension;import recipe.domain.Recipe;import java.util.Optional;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.junit.jupiter.api.Assertions.assertNull;@ExtendWith(SpringExtension.class)@DataJpaTestclass RecipeRepositoryIT {    @Autowired    private RecipeRepository recipeRepository;    private final String desc = "The Best guacamole! So easy to make with ripe avocados!";    private final Long idValue = 1l;    @BeforeEach    public void saveRecipe() {        Recipe perfectGuacamole = new Recipe();        perfectGuacamole.setId(idValue);        perfectGuacamole.setDescription(this.desc);        this.recipeRepository.save(perfectGuacamole);    }    @Test    void deleteRecipe() {        Optional<Recipe> recipeOptional = recipeRepository.findBydescription(desc);        this.recipeRepository.delete(recipeOptional.get());        recipeOptional = recipeRepository.findBydescription(desc);        assertEquals(Optional.empty(),recipeOptional);    }    @Test    void findByDescription() {        final Optional<Recipe> recipeOptional = this.recipeRepository.findBydescription(desc);        assertEquals(this.desc, recipeOptional.get().getDescription());    }}